import{_ as s,c as n,o as e,ag as t}from"./chunks/framework.CDhRSyRB.js";const b=JSON.parse('{"title":"linux帮助命令","description":"","frontmatter":{"title":"linux帮助命令","tags":["linux"],"categories":"linux","abbrlink":"354e0293"},"headers":[],"relativePath":"main/Linux/帮助命令.md","filePath":"main/Linux/帮助命令.md","lastUpdated":1662368311000}'),l={name:"main/Linux/帮助命令.md"};function i(p,a,r,d,c,h){return e(),n("div",null,a[0]||(a[0]=[t(`<h2 id="帮助命令" tabindex="-1">帮助命令 <a class="header-anchor" href="#帮助命令" aria-label="Permalink to &quot;帮助命令&quot;">​</a></h2><h3 id="内部命令" tabindex="-1">内部命令 <a class="header-anchor" href="#内部命令" aria-label="Permalink to &quot;内部命令&quot;">​</a></h3><p>内建命令：实际上是 shell 程序的一部分，其中包含的是一些比较简单的 Linux 系统命令，这些命令是写在 bash 源码的 builtins 里面的，由 shell 程序识别并在 shell 程序内部完成运行，通常在 Linux 系统加载运行 时 shell 就被加载并驻留在系统内存中。而且解析内部命令 shell 不需要创建子进程，因此其执行速度比外部 命令快。比如：history、cd、exit 等等。</p><h3 id="外部命令" tabindex="-1">外部命令 <a class="header-anchor" href="#外部命令" aria-label="Permalink to &quot;外部命令&quot;">​</a></h3><p>外部命令：是 Linux 系统中的实用程序部分，因为实用程序的功能通常都比较强大，所以其包含的程序量也 会很大，在系统加载时并不随系统一起被加载到内存中，而是在需要时才将其调入内存。虽然其不包含在 shell 中，但是其命令执行过程是由 shell 程序控制的。外部命令是在 Bash 之外额外安装的，通常放 在/bin，/usr/bin，/sbin，/usr/sbin 等等。比如：ls、vi 等。</p><h3 id="区分内部命令-外部命令" tabindex="-1">区分内部命令，外部命令 <a class="header-anchor" href="#区分内部命令-外部命令" aria-label="Permalink to &quot;区分内部命令，外部命令&quot;">​</a></h3><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>#使用type 命令</span></span>
<span class="line"><span>例如 cd 是内部命令则输入</span></span>
<span class="line"><span></span></span>
<span class="line"><span>$type cd</span></span>
<span class="line"><span>cd 是 shell 内嵌</span></span>
<span class="line"><span></span></span>
<span class="line"><span>则会出现说这样的内容，则 说明cd时内部命令</span></span>
<span class="line"><span>如果输入</span></span>
<span class="line"><span></span></span>
<span class="line"><span>$type vi</span></span>
<span class="line"><span>vi 是 /usr/bin/vi</span></span>
<span class="line"><span></span></span>
<span class="line"><span>则会出现这样一个路径，则是外部命令，前面有说到外部命令都存在/bin，/user/bin，/sbin，/user/sbin</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br></div></div><h3 id="help-命令" tabindex="-1">help 命令 <a class="header-anchor" href="#help-命令" aria-label="Permalink to &quot;help 命令&quot;">​</a></h3><p>help 是用于查询shell内部命令的，可以查看帮助信息,</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>#内部命令查询帮助命令</span></span>
<span class="line"><span>$help 命令</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>外部命令提供了一个参数 -help</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>#命令 -help</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="man-命令" tabindex="-1">man 命令 <a class="header-anchor" href="#man-命令" aria-label="Permalink to &quot;man 命令&quot;">​</a></h3><p>man命令也是帮助命令，但是不区分内部和外部命令</p><table tabindex="0"><thead><tr><th>章节数</th><th>说明</th></tr></thead><tbody><tr><td>1</td><td>Standard commands （标准命令）</td></tr><tr><td>2</td><td>System calls （系统调用）</td></tr><tr><td>3</td><td>Library functions （库函数）</td></tr><tr><td>4</td><td>Special devices （设备说明）</td></tr><tr><td>5</td><td>File formats （文件格式）</td></tr><tr><td>6</td><td>Games and toys （游戏和娱乐）</td></tr><tr><td>7</td><td>Miscellaneous （杂项）</td></tr><tr><td>8</td><td>Administrative Commands （管理员命令）</td></tr><tr><td>9</td><td>其他（Linux 特定的）， 用来存放内核例行程序的文档。</td></tr></tbody></table><h3 id="info-命令" tabindex="-1">info 命令 <a class="header-anchor" href="#info-命令" aria-label="Permalink to &quot;info 命令&quot;">​</a></h3>`,16)]))}const u=s(l,[["render",i]]);export{b as __pageData,u as default};
